scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile44`, function (sprite, location) {
    tiles.setTileAt(location, tiles.tileImageAtLocation(tiles.getTileLocation(location.column, location.row - 1)))
    sprites.setDataBoolean(sprite, "collectedStickAttack", true)
    extraEffects.createSpreadEffectAt(extraEffects.createSingleColorSpreadEffectData(7, ExtraEffectPresetShape.Explosion), location.x, location.y, 100)
})
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile13`, function (sprite, location) {
    endLevel(sprite)
})
sprites.onOverlap(SpriteKind.Hitbox, SpriteKind.EnemyProjectile, function (sprite, otherSprite) {
    ninjiTakeDamage(sprite)
})
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile50`, function (sprite, location) {
    sprites.setDataNumber(sprite, "hp", 0)
    ninjiTakeDamage(sprite)
    tileUtil.replaceAllTiles(assets.tile`myTile50`, assets.tile`myTile59`)
    tileUtil.coverAllTiles(assets.tile`myTile59`, assets.tile`myTile50`)
})
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile54`, function (sprite, location) {
    tiles.setTileAt(location, tiles.tileImageAtLocation(tiles.getTileLocation(location.column, location.row - 1)))
    if (points < 999990) {
        points += 10
    }
})
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile12`, function (sprite, location) {
    endLevel(sprite)
})
function fadeScreen () {
    for (let index = 0; index <= 3; index++) {
        shader2 = shader.createRectangularShaderSprite(320, 240, shader.ShadeLevel.One)
        shader2.lifespan = index * 100 + 1000
        shader2.z = 20
        shader2.setFlag(SpriteFlag.RelativeToCamera, true)
        pause(100)
    }
}
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile10`, function (sprite, location) {
    endLevel(sprite)
})
