game.onUpdate(function () {
    for (let value of sprites.allOfKind(SpriteKind.StickSlash)) {
        if (platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.FacingLeft))) {
            if (platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.WallSliding)) || platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.Turning))) {
                value.setPosition(ninjiHitbox.x + 12, ninjiHitbox.y)
            } else {
                value.setPosition(ninjiHitbox.x - 12, ninjiHitbox.y)
            }
        } else if (platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.FacingRight))) {
            if (platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.WallSliding)) || platformer.hasState(ninjiHitbox, platformer.rule(platformer.PlatformerSpriteState.Turning))) {
                value.setPosition(ninjiHitbox.x - 12, ninjiHitbox.y)
            } else {
                value.setPosition(ninjiHitbox.x + 12, ninjiHitbox.y)
            }
        }
    }
})
game.onUpdate(function () {
    if (gameRunning) {
        fancyText.setText(myTextSprite, "x" + convertToText(lives))
        fancyText.setText(myTextSpriteShadow, "x" + convertToText(lives))
        fancyText.setText(myTextSprite2, convertToText(points))
        fancyText.setText(myTextSpriteShadow2, convertToText(points))
    }
})
game.onUpdate(function () {
    for (let value of sprites.allOfKind(SpriteKind.Player)) {
        for (let value2 of sprites.allOfKind(SpriteKind.Hitbox)) {
            value.setPosition(value2.x, value2.y)
        }
    }
})
game.onUpdate(function () {
    if (gameRunning) {
        if (movementEnabled) {
            if (onLadder) {
                platformer.moveSprite(ninjiHitbox, true, 0)
                platformer.setFeatureEnabled(platformer.PlatformerFeatures.WallJumps, false)
                platformer.setFeatureEnabled(platformer.PlatformerFeatures.MovementMomentum, false)
                platformer.setGravity(0)
                controller.moveSprite(ninjiHitbox, 0, 100)
            } else {
                platformer.moveSprite(ninjiHitbox, true, 150)
                platformer.setFeatureEnabled(platformer.PlatformerFeatures.WallJumps, true)
                platformer.setFeatureEnabled(platformer.PlatformerFeatures.MovementMomentum, true)
                platformer.setGravity(750)
                controller.moveSprite(ninjiHitbox, 0, 0)
            }
        } else {
            platformer.moveSprite(ninjiHitbox, true, 0)
            platformer.setFeatureEnabled(platformer.PlatformerFeatures.WallJumps, false)
            platformer.setFeatureEnabled(platformer.PlatformerFeatures.MovementMomentum, false)
            platformer.setGravity(750)
            controller.moveSprite(ninjiHitbox, 0, 0)
            if (platformer.hasState(ninjiHitbox, platformer.PlatformerSpriteState.OnGround)) {
                ninjiHitbox.vx = 0
            }
        }
    }
})
