forever(function () {
    for (let value of tiles.getTilesByType(assets.tile`myTile56`)) {
        if (ninjiHitbox.tileKindAt(TileDirection.Center, assets.tile`myTile51`) && !(ninjiHitbox.tileKindAt(TileDirection.Center, tiles.tileImageAtLocation(value)))) {
            if (controller.up.isPressed() || controller.down.isPressed()) {
                if (!(onLadder)) {
                    onLadder = true
                    tiles.setWallAt(tiles.getTileLocation(value.column, value.row + 1), false)
                    ninjiHitbox.setPosition(ninjiHitbox.tilemapLocation().column * 16 + 8, ninjiHitbox.y)
                }
            }
        } else if (ninjiHitbox.tileKindAt(TileDirection.Bottom, assets.tile`myTile51`)) {
            if (controller.down.isPressed()) {
                if (!(onLadder)) {
                    onLadder = true
                    tiles.setWallAt(tiles.getTileLocation(value.column, value.row + 1), false)
                    ninjiHitbox.setPosition(ninjiHitbox.tilemapLocation().column * 16 + 8, ninjiHitbox.y + 16)
                }
            } else {
                onLadder = false
                tiles.setWallAt(tiles.getTileLocation(value.column, value.row + 1), true)
            }
        } else {
            onLadder = false
            tiles.setWallAt(tiles.getTileLocation(value.column, value.row + 1), true)
        }
    }
})
